import React, {useEffect, useState} from 'react';
import Tmdb from './Tmdb';

export default () => {

  const [movieList, setMovieList] = useState([]);

  useEffect(() =>{
    const loadAll = async () =>{
      //Pegando a lista Total
      let list = await Tmdb.getHomeList();
      setMovieList(list);
    }

    loadAll();

  }, []);

  return (
    <div class="page"></div>
  )
}
