import React, {useEffect, useState} from 'react';
import './App.css';
import Tmdb from './Tmdb';
import MovieRow from './componentes/MovieRow';
import FeaturedMovie from './componentes/FeaturedMovie';

export default () => {

  const [movieList, setMovieList] = useState([]);
  const [featuredData, setFeaturedData] = useState(null);


  useEffect(() =>{
    const loadAll = async () =>{
      //Pegando a lista Total
      let list = await Tmdb.getHomeList();
      setMovieList(list);

      //Pegando o filme em destaque Fwatured
      let originals = list.filter(i.slug === 'originals');
      let radomChosen = match.floor(match.random() * (originals.[0].items.results.length -1))
      let chosen = originals[0].items.results[radomChosen];
      console.log(chosen);
    }

    loadAll();

  }, []);

  return (
    <div className="page">

      {featuredData &&
        <FeaturedMovie item={featuredData} />
      }

      <section className="lists">
        {movieList.map((item, key) => (
          <MovieRow key={key} title={item.title} items={item.items} />
        ))}
      </section>
    </div>
  )
}
